Root__Blog_Post.Content:
{% assign data = Model.Data %}
{% assign rootCategories = "Root" | get_blog_categories %}

{% assign post = data.Id | get_posts_by_id %}
{% assign category = post.CategoryId | get_blog_categories_by_id %}
{% assign breadcrumbList = category | category_to_breadcrumb %}

{% assign imageDecor = "Web-17/decor-primary-full.svg" | generate_proxy_url: width: null, height: null %}
{% assign homeImageDecor = "Web-17/decor-white.svg" | generate_proxy_url: width: null, height: null %}

{% assign topPosts = "Desc" | get_posts_by_view %}
{% assign trendingPosts = 7 | get_trending_posts_date_to_date: offset: 0 %}

{% assign image404Url = "404Page/No-data.png" | generate_proxy_url: width: null, height: null %}
{% assign defaultHomepageUrl = "~/" | absolute_url %}

{% assign displayDate = post.DateUpdated | default: post.DatePublished | date: "%d THG %m, %Y" %}

{% style name: "tds-editor", version: "2", at: "Head" %}
{% script name: "tds-editor", version: "2", at: "Head" %}

{% zone "Header" %}
{{ 'Module__MenuWrapper' | shape_new | shape_properties: Shape: 'FE__Menu' | shape_render }}
{% endzone %}

{% zone "Footer" %}
{{ "Module__FooterWrapper" | shape_new | shape_properties: Shape: 'FE__Footer' | shape_render }}
{% endzone %}

<main>
    <section>
        <div class="bg-primary-4 mt-20 overflow-hidden">
            <div class="mx-auto w-full xl:max-w-7xl">
                {{ 'Module__Blog_BreadcrumbWrapper' | shape_new | shape_properties: Shape: 'FE__Breadcrumb', Data:
                breadcrumbList | shape_render }}
            </div>
        </div>
    </section>
    <section>
        <div class="mx-auto w-full relative overflow-hidden xl:overflow-visible xl:max-w-7xl">
            <div class="absolute hidden -top-[5%] w-[1000px] h-[1000px] md:block lg:-z-10 lg:-left-[35%]">
                <img src="{{ imageDecor }}"
                    class="w-full h-full"
                    alt="">
            </div>
            <div
                class="flex flex-col gap-16 py-16 px-4 md:gap-20 md:px-6 md:py-20 lg:gap-8 lg:flex-row lg:px-2 2xl:px-0 lg:pb-[120px]">
                <div class="flex flex-col gap-6 overflow-x-hidden lg:w-2/3 animate-top"
                    style="animation-delay: 200ms; animation-fill-mode: backwards;">
                    {{ 'Module__Blog_PostListWrapper' | shape_new | shape_properties: Shape: 'FE__Blog_PostDetail',
                    Data: post | shape_render }}
                </div>
                <div class="flex flex-col gap-8 bg-white lg:w-1/3 lg:sticky lg:top-20 lg:h-full animate-top"
                    style="animation-delay: 400ms; animation-fill-mode: backwards;">
                    {{ 'Module__Blog_PostListWrapper' | shape_new | shape_properties: Shape: 'FE__Blog_Sidebar_Right',
                    Category: rootCategories, Data: topPosts | shape_render }}
                </div>
            </div>
        </div>
    </section>

</main>

Root__Blog_Suggest.Content:
{% assign blog = Model.Data %}
{% assign currentCategory = blog.CurrentCategoryId | get_blog_categories_by_id %}
{% assign breadcrumbList = currentCategory | category_to_breadcrumb %}
{% assign imageDecor = "Web-17/decor-primary-full.svg" | generate_proxy_url: width: null, height: null %}

{% assign image404Url = "404Page/No-data.png" | generate_proxy_url: width: null, height: null %}
{% assign defaultHomepageUrl = "~/" | absolute_url %}

{% assign minusPageIndex = blog.PageIndex | minus: 1 %}
{% assign minusOffSet = minusPageIndex | times: blog.PageSize %}
{% assign postsCategory = "Newest" | get_posts: offset: minusOffSet, limit: 7, nested: true %}

{% zone "Header" %}
  {{ 'Module__MenuWrapper' | shape_new | shape_properties: Shape: 'FE__Menu' | shape_render }}
{% endzone %}

{% zone "Footer" %}
  {{ "Module__FooterWrapper" | shape_new | shape_properties: Shape: 'FE__Footer' | shape_render }}
{% endzone %}

{{ "Đề xuất" | page_title_segment }}
{{ Site.SiteName | page_title_segment: position: "end" }}

<main>
  <section>
    <div class="mx-auto w-full relative overflow-clip overflow-y-visible xl:max-w-[1920px]">
      <div class="absolute -z-10 -left-1/3 -top-1/4 md:-right-[15%] md:inset-auto lg:right-[10%] lg:-top-1/4 xl:-left-[5%]">
        <img src="{{ imageDecor | raw }}"class="w-[1083px] h-[878px] xl:w-[1151px] xl:h-[1253px]"alt="{{ imageDecor | raw }}">
      </div>
      <div class="flex justify-between items-center mt-20 px-4 py-16 md:px-6 md:py-20 lg:gap-4 lg:py-0 lg:pl-2 lg:pr-0 2xl:px-0 animate-right">
        <div class="flex flex-col gap-6 2xl:ml-[320px]">
          {% assign breadcrumbList = '' | split: ',' | init_breadcrumb: lastTitle: 'Bài viết đề xuất' %}
          {{ 'Module__Blog_BreadcrumbWrapper' | shape_new | shape_properties: Shape: 'FE__Breadcrumb', Data: 
           breadcrumbList | shape_render }}
          <div class="text-display-5 font-semibold text-primary-1 max-w-screen-sm md:text-display-4">
            {{ ThemeSettings.suggestBreadcrumbTitle | raw }}
          </div>
        </div>
        <div class="hidden w-[44%] lg:flex">
          <div class="relative w-full pt-[105%]">
            <img
              alt=""
              src="{{ ThemeSettings.suggestImageBreadcrumb.fullUrl }}"
              class="image-custom object-cover">
          </div>
        </div>
      </div>
    </div>
  </section>

  <section>

    <div class="w-full bg-white">
      <div class="w-full mx-auto xl:max-w-7xl">
        <div class="flex flex-col gap-10 px-6 py-10 lg:px-2 xl:pt-[140px] xl:pb-40 2xl:px-0">
          {% form action: 'Search', controller: 'TopicV2', method: 'get', 
 class: 'flex items-center justify-between border border-neutral-2-200 rounded-full text-body-1 px-2.5 py-1.5 w-full md:w-1/2 xl:w-1/3 reveal fadeInUp' %}
            <input
              name="key"
              type="text"
              value="{{ Request.Query.key[0] | raw }}"
              class="focus:outline-none placeholder:text-neutral-1-400 text-neutral-1-900 flex-1"
              required
              placeholder="Nhập từ khóa tìm kiếm"
              id="input-search">
            <button
              onclick="delete_text()"
              class="btn_clear"
              type="button"></button>
          {% endform %}

          {{ 'Module__Blog_PostWrapper' | shape_new | shape_properties: Shape: 'FE__Post_Category', Data: 
           postsCategory | shape_render }}

          {{
             "Module__Blog_PaginationWrapper"
           | shape_new
           | shape_properties:
           pageIndex: postsCategory.PageIndex,
           PageSize: 7,
           Total: postsCategory.TotalResult,
           Shape: "FE__Blog_Pagination"
           | shape_render }}
        </div>
      </div>
    </div>
  </section>
</main>
<script>
  function delete_text() {
    document.getElementById('input-search').value = '';
  }
</script>

