<main>
  <section>
    <div class="bg-primary-4 mt-20">
      <div class="mx-auto w-full xl:max-w-7xl animate-top">
        {% assign breadcrumbList = '' | split: ',' | init_breadcrumb: lastTitle: 'Liên hệ' %}
        {{ 'Module__Blog_BreadcrumbWrapper' | shape_new | shape_properties: Shape: 'FE__Breadcrumb', Data: 
         breadcrumbList | shape_render }}
      </div>
    </div>
  </section>
  <section>
    <div class="mx-auto w-full relative overflow-hidden xl:overflow-visible xl:max-w-7xl">
      <div class="absolute -z-10 -right-[60%] w-[532px] h-[580px] md:right-auto md:left-[8%] xl:-left-[20%]">
        <img src="{{ imageDecor }}"class="w-full h-full"alt="{{ imageDecor | raw }}">
      </div>
