                <p class="text-neutral-1-600 font-medium text-lg">Địa chỉ</p>
              </div>
              <p class="text-neutral-1-900 text-body-1 pl-7">{{ ThemeSettings.address | raw }}</p>
            </div>
          </div>
        </div>

        <div class="xl:w-1/2 animate-right1" style="animation-delay: 200ms; animation-fill-mode: backwards;">
          {% form action: 'ContactUs', controller: 'ContactUs', area: "TheWeb17", method: 'post', class: "flex flex-col
          gap-6" %}
            <div class="flex flex-col gap-6 border border-primary-1 rounded-tl-[40px] rounded-br-[40px] p-6">
              <div>{% render_section "Messages", required: false %}</div>
              <div class="grid gap-6 md:grid-cols-2">
                <div class="flex flex-col gap-2">
                  <p class="text-secondary-1 text-body-1 font-medium">Họ và tên</p>
                  <input
                    name="FullName"
                    id="FullName"
                    type="text"
                    placeholder="Nhập họ và tên của bạn"
                    class="placeholder:text-neutral-1-600 text-body-1 text-neutral-1-900 focus:outline-none border-b border-neutral-2-200 p-2">
