                    name="Email"
                    id="Email"
                    type="email"
                    placeholder="Nhập email của bạn"
                    class="placeholder:text-neutral-1-600 text-body-1 text-neutral-1-900 focus:outline-none border-b border-neutral-2-200 p-2">
                </div>
                <div class="flex flex-col gap-2">
                  <p class="text-secondary-1 text-body-1 font-medium">Số điện thoại</p>
                  <input
                    type="tel"
                    name="PhoneNumber"
                    id="phoneNumber"
                    pattern="[0-9]{10}"
                    required
                    placeholder="Nhập số điện thoại của bạn"
                    class="placeholder:text-neutral-1-600 text-body-1 text-neutral-1-900 focus:outline-none border-b border-neutral-2-200 p-2">
                </div>
                <div class="flex flex-col gap-2">
                  <p class="text-secondary-1 text-body-1 font-medium">Tiêu đề</p>
                  <input
                    type="text"
                    name="title"
                    id="title"
                    placeholder="Nhập tiêu đề"
                    class="placeholder:text-neutral-1-600 text-body-1 text-neutral-1-900 focus:outline-none border-b border-neutral-2-200 p-2">
                </div>
              </div>
              <div class="flex flex-col gap-2">
                <p class="text-secondary-1 text-body-1 font-medium">Message</p>
                <input
                  type="text"
                  name="Message"
                  id="message"
                  placeholder="Nhập nội dung"
                  class="placeholder:text-neutral-1-600 text-body-1 text-neutral-1-900 focus:outline-none border-b border-neutral-2-200 p-2">
              </div>
            </div>
            <div class="flex justify-end">
              <button
                name="submit"
                type="submit"
                data-submit="...Sending"
                class="text-white text-body-1 font-medium px-16 py-[9px] bg-primary-1 rounded-full">Gửi</button>
            </div>
          {% endform %}

        </div>
      </div>
    </div>
